mapper.delete:
delete from t_exam
WHERE id IN

    #{examId}

delete from t_exam_grade
WHERE exam_id IN

    #{examId}

delete from t_exam_repo
WHERE exam_id IN

    #{examId}

delete from t_exam_question
WHERE exam_id IN

    #{examId}

mapper.select:
select id as examId, title as examTitle, case when saq_count > 0 then 1 else 0 end as neededMark
from t_exam

    where user_id = #{userId}
