mapper.select:
SELECT ug.grade_id AS gradeId, e.id AS exam_id, e.title AS exam_title, g.grade_name, MAX(ues.user_score) AS max_score, MIN(ues.user_score) AS min_score, AVG(ues.user_score) AS avg_score, COUNT(ues.user_id) AS attendNum FROM t_exam e JOIN t_exam_grade ug ON e.id = ug.exam_id JOIN t_user_exams_score ues ON e.id = ues.exam_id and ues.whether_mark in (-1,1) JOIN t_user u ON ues.user_id = u.id AND u.grade_id = ug.grade_id JOIN t_grade g ON ug.grade_id = g.id
ues.user_id IS NOT NULL
and ug.grade_id = #{gradeId}
and e.title like concat('%',#{examTitle},'%')
and g.user_id = #{userId}
GROUP BY ug.grade_id, e.id, e.title

select ues.id, e.title, u.real_name, ues.user_time, ues.user_score, ues.limit_time, ues.count from t_user_exams_score as ues join t_user as u on ues.user_id = u.id join t_exam as e on ues.exam_id = e.id
u.grade_id = #{gradeId} and ues.exam_id = #{examId}
and u.real_name like concat('%',#{realName},'%')

select u.real_name, g.grade_name, ues.user_score as score
from t_user_exams_score as ues
join t_user as u on ues.user_id = u.id
join t_grade as g on u.grade_id = g.id
where ues.exam_id = #{examId}
and g.id = #{gradeId}
order by ues.user_score desc;
