mapper.select:
select ues.id,eg.grade_id,eg.exam_id, e.title as examTitle,g.grade_name,max(ues.user_score) as maxScore, min(ues.user_score) as minScore, avg(ues.user_score) as avgScore, count(ues.user_score) as attendNum from t_exam_grade as eg join t_user_exams_score as ues on eg.exam_id = ues.exam_id and (ues.whether_mark = -1 or ues.whether_mark = 1) join t_user as u on ues.user_id = u.id and u.role_id = 1 join t_exam as e on eg.exam_id = e.id join t_grade as g on eg.grade_id = g.id
eg.grade_id = #{gradeId}
and e.title like concat('%',#{examTitle},'%')
g.user_id = #{userId}

select ues.id, e.title, u.real_name, ues.user_time, ues.user_score, ues.limit_time, ues.count from t_user_exams_score as ues join t_user as u on ues.user_id = u.id join t_exam as e on ues.exam_id = e.id
u.grade_id = #{gradeId} and ues.exam_id = #{examId}
and u.real_name like concat('%',#{realName},'%')

select u.real_name, g.grade_name, ues.user_score as score
from t_user_exams_score as ues
join t_user as u on ues.user_id = u.id
join t_grade as g on u.grade_id = g.id
where ues.exam_id = #{examId}
and g.id = #{gradeId}
order by ues.user_score desc;
